import React, {Component} from 'react';
import Layout from '../../layouts/layout2';
import data from '../../components/data';
import Link from 'next/link'

function Section(){
  return(
    <section class="laptop" id="hp">
      <div class="teks7">HANDPHONE</div>

      <div class="row">
        {data.hp.map((data, index)=>
          <div key={index}>
            <div class="col-12 col-s-12">
              <div class="col-2 col-s-12">
                <Link href="../artikel/[kode]" as="artikel/{data.nama}"><a>
              <img src={data.gambar} /></a></Link>
              </div>
              <div class="col-10 col-s-12">
                <h4>{data.nama}</h4>
                <p>{data.deskripsi}
                <br/><em><strong>{data.harga}</strong></em></p>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

class Hp extends Component{
  render(){
    return (
        <Layout>
          <Section />
        </Layout>
    );
  }
}

export default Hp;
