import React, {Component} from 'react';
import Layout from '../layouts/layout1';

function Header(){
  return(
    <header class="testimoni1">
        <h1 class="teks7">Review Customer</h1>
        <div class="row">
          <div class="col-4 col-s-12">
            <img src="/images/review/ppl1.jpg" />
            <p><b>Affendi</b> | CEO PT. Firma Jaya<br /><i>"Saya sangat puas belanja di sini"</i></p>
          </div>
          <div class="col-4 col-s-12">               
            <img src="/images/review/ppl2.jpg" />
            <p><b>Wulan</b> | Manajer CV. Maspindo<br /><i>"Pelayanannya ramah banget"</i></p>
          </div>
          <div class="col-4 col-s-12">                     
            <img src="/images/review/ppl3.jpg" />
            <p><b>Erick</b> | Youtuber<br /><i>"Letaknya strategis dan harganya jempol"</i></p>
          </div>
        </div>
    </header>
  );
}

function Section1(){
  return(
    <section>
        <h2>Beritahu Kami Review Anda</h2>
        <textarea placeholder="Bagaimana pendapat anda mengenai DigitalQ ?"></textarea><br />
        <button>KIRIM</button>
    </section>
  );
}

function Section2(){
    return(
      <section>
        <ul>
            <li>
                <img src="review/unknown.jpg" />
                <br /><b>Dewi & Afifa</b> | <time>Minggu, 6 Juni 2020</time><br /><i>Laptop yang dijual bagus-bagus dengan harga lebih murah dari tempat lain.</i>
            </li>
        </ul>
        <hr></hr>
      </section>
    );
}

class Review extends Component{
  render(){
    return (
      <Layout>
        <Header />
        <Section1 />
        <Section2 />
      </Layout>
    );
  }
}

export default Review;
