import React, {Component} from 'react';
import Layout from '../../layouts/layout1';
import Menu from '../../components/menu';
import data from '../../components/data';

function Section(){
  return(
    <section class="laptop" id="laptop">
      <div class="teks7">LAPTOP</div>

      <div class="row">
        {data.laptop.map((data, index)=>
          <div key={index}>
            <div class="col-12 col-s-12">
              <div class="col-2 col-s-12">
                <img src={data.info.gambar} />
              </div>
              <div class="col-10 col-s-12">
                <h4>{data.nama}</h4>
                <p>{data.info.deskripsi}
                <br/><em><strong>{data.info.harga}</strong></em></p>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

class Laptop extends Component{
  render(){
    return (
      <Layout>
        <Menu laptop="produk" hp="produk/hp" dll="produk/dll" />
        <Section />
      </Layout>
    );
  }
}

export default Laptop;
