import dummyData from '../../components/data.json';
import Login from '../../pages/login';

const Artikel = (props) => {
    return(
        <center>
        <div>
      
            <div>
                <p>Anda akan memesan</p>
                <h1>{props.nama}</h1>
                <p>{props.deskripsi}</p><br/>
                <p>{props.harga}</p><br/>
                <h4>silahkan melakukan login terlebih dahulu</h4>
            </div>
       
        <br/><Login/></div> </center>
    );
}

export async function getStaticProps(context){
    let kode = context.params.kode;
    let data = dummyData.find(x => x.nama == kode);
    let {nama, gambar, deskripsi, harga}= data;
    return{
        props:{
            nama, gambar, deskripsi, harga
        }
    }
}

export async function getStaticPaths(){
   
    return{
        paths : dummyData.map(data =>(
            {
                params : {kode: `${data.nama}`}}
            
        )),
        fallback : false
    }
}

export default Artikel;
