import React, {Component} from 'react';

function Navbar(){
  return(
    <nav>
      <ul>
        <li><a href="index"><h3>DigitalQ</h3></a></li>
        <li><a href="index#about">About</a></li>
        <li><a href="index#product">Product</a></li>
        <li><a href="#contact">Contact</a></li>
        <li><a href="#">Review</a></li>
        <li><a href="login">LOGIN</a></li>
      </ul>
      <hr></hr>
    </nav>
  );
}

function Header(){
  return(
    <header>
        <h2>Review Customer</h2>
        
        <img src="review/ppl1.jpg" />
        <p><b>Affendi</b> | CEO PT. Firma Jaya<br /><i>"Saya sangat puas belanja di sini"</i></p>
                            
        <img src="review/ppl2.jpg" />
        <p><b>Wulan</b> | Manajer CV. Maspindo<br /><i>"Pelayanannya ramah banget"</i></p>
                           
        <img src="review/ppl3.jpg" />
        <p><b>Erick</b> | Youtuber<br /><i>"Letaknya strategis dan harganya jempol"</i></p>

        <hr></hr>
    </header>
  );
}

function Section1(){
  return(
    <section id="about">
      <u>Sekilas Tentang Kami</u>
      <p><b>DigitalQ </b>merupakan toko elektronik terpercaya yang menjual bervariasi laptop dan handphone berkualitas dengan harga terjangkau. DigitalQ siap menjamin kepuasan pelanggan.</p>
      <img src="2.jpg" />
      <hr></hr>
    </section>
  );
}

function Footer(){
  return(
    <footer id="contact">
      <p><b>HUBUNGI KAMI</b></p>
        <ul>
          <li>082 345 678 910</li>
          <li>email : digital1@gmail.com</li>
        </ul>
      <p><b>KUNJUNGI KAMI</b></p>
        <ul>
          <li>Jl. Ahmad Yani, Banyuwangi, Jawa Timur</li>
        </ul>
      <p><b>PEMBAYARAN</b></p>
        <ul>
          <li><img src="bayar1.png" /></li>
          <li><img src="bayar2.png" /></li>
          <li><img src="bayar3.png" /></li>
          <li><img src="bayar4.png" /></li>
          <li><img src="bayar5.png" /></li>
        </ul>
      <p>© DigitalQ 2020. Hak Cipta Dilindungi</p>
      <p>Negara : Indonesia</p>
    </footer>
  );
}

class Review extends Component{
  render(){
    return (
      <div>
        <Navbar />
        <Header />
        <Section1 />
        <Footer/>
      </div>
    );
  }
}

export default Review;
